  )
  )
}

function meuComponent2(){
  return React.createElement('div',{className: 'Component 2' },
    React.createElement(meuComponent3)
  )
}

function meuComponent3(){
  return React.createElement('div',{className: 'Component 3'}, 
    React.createElement(meuComponent4)
  )
}     

function meuComponent4(props){
  return React.createElement('p',null,`Meu nome é ${props.nome}`)
}

function meuComponent() {
  return React.createElement('div',{className: 'container'},
    React.createElement(meuComponent1)) 
}

ReactDOM.render( 
  React.createElement(meuComponent),
  document.getElementById('root')
);
