import React, { createElement } from 'react';
import ReactDOM from 'react-dom';

const NomeContext =  React.createContext('meuNome')//Funcao para propagar uma propriedade para diversos components

function meuComponent1() {
  const meuNome = 'Aldair'
  return (
    React.createContext(NomeContext.Provider,
      {value: meuNome},
    )
  )
}

function meuComponent2(){
  return (
    React.createElement('div',{className: 'Component 2' },
      React.createElement(meuComponent3)
    )
  )
}

function meuComponent3(){
  return (
    React.createElement('div',{className: 'Component 3'}, 
      React.createElement(meuComponent4)
    )
  )
}     

function meuComponent4(){
  return (
    React.createContext(NomeContext.Consumer,null,
      (nomeContext) => {
        React.createElement('p',null,`Meu nome é ${nomeContext}`)
      }
    )
  )
}

function meuComponent() {
  return (
    React.createElement('div',{className: 'container'},
      React.createElement(meuComponent1)
    )
  ) 
}

ReactDOM.render( 
  React.createElement(meuComponent),
  document.getElementById('root')
);
