import React, { createElement } from 'react';
import ReactDOM from 'react-dom';

const NomeContext = React.createContext('nome') //Retorna um componente com dois metodos Provider e Consumer

function MeuComponent1() {
  const meuNome = 'Aldair Andre' 
  return(
    <NomeContext.Provider value={meuNome}>
      <div className='Component 1'>
        <MeuComponent2/>
      </div>
    </NomeContext.Provider>
  )
}

function MeuComponent2(){
  return(
    <div className='Component 2'>
      <MeuComponent3/>
    </div>
  )
}

function MeuComponent3(){
  return(
    <div className='Component 3'>
      <MeuComponent4/>
    </div>
  )
}     

function MeuComponent4(){
  return(
    <NomeContext.Consumer>
      {
        (nomeContext) => {
          <div className='Component 4'>
            <p className='pComponet4'>
              Ola meu nome é {nomeContext}
            </p>
          </div>
        }
      }
    </NomeContext.Consumer>
  )
}

function meuComponent() {
  return (
    React.createElement('div',{className: 'container'},
      React.createElement(meuComponent1)
    )
  ) 
}

ReactDOM.render( 
  React.createElement(meuComponent),
  document.getElementById('root')
);
